mapper.update:
UPDATE route
SET image_url = #{imageUrl}
WHERE user_id = #{userId}
  AND start_time = #{startTime}

UPDATE daily_record
SET duration = (
    SELECT IFNULL(SUM(duration), 0)
    FROM route
    WHERE user_id = #{userId}
      AND DATE(created_at) = CURRENT_DATE
)
WHERE user_id = #{userId}
  AND date = CURRENT_DATE;
